            let randomDeg = Math.floor(500 + Math.random() * 2000); 
            currentRotation += randomDeg;
            document.getElementById('wheel').style.transform = `rotate(${currentRotation}deg)`;
            setTimeout(() => {
                resBox.innerText = selected.t;
                addScore(1);
